nta.declaration:
// Place global declarations here.
bool visited[3][3];

bool all_visited() {
    int i,j;
    for (i = 0; i < 3; i++) {
        for (j = 0; j < 3; j++) {
            if (!visited[i][j])
                return false;
        }
    }
    return true;
}

template.declaration:
// Place local declarations here.
clock t, total;
int x = 0, y = 0;
int dx = 0, dy = 1;
int dir = 0;

bool turn_states[3][3][4];

void initialize1() {
    int i,j,k;
    for (i = 0; i < 3; i++) {
        for (j = 0; j < 3; j++) {
            for (k = 0; k < 4; k++) {
                turn_states[i][j][k] = true;
            }
        }
    }

    turn_states[0][0][1] = false;
    turn_states[1][0][1] = false;
    turn_states[1][0][3] = false;
    turn_states[2][0][0] = false;
    turn_states[2][1][0] = false;
    turn_states[2][2][3] = false;
    turn_states[1][2][3] = false;
    turn_states[0][2][2] = false;
    turn_states[0][1][1] = false;
    turn_states[1][1][2] = false;
}


void initialize2() {
    int i,j,k;
    for (i = 0; i < 3; i++) {
        for (j = 0; j < 3; j++) {
            for (k = 0; k < 4; k++) {
                turn_states[i][j][k] = false;
            }
        }
    }

    turn_states[0][0][3] = true;
    turn_states[0][1][0] = true;
    turn_states[2][1][1] = true;
    turn_states[2][0][2] = true;
    turn_states[2][0][3] = true;
    turn_states[2][2][0] = true;
    turn_states[2][2][1] = true;
    turn_states[2][2][2] = true;
    turn_states[0][2][3] = true;
    turn_states[0][2][0] = true;
    turn_states[1][2][1] = true;
    turn_states[1][0][2] = true;
}

bool is_valid(int a) {
    return a >= 0 && a <= 2;
}

void turn() {
    dir = (dir+1) % 4;

    if (dir % 2 == 0) {
        dx = 0;
        dy = 1;
    }
    else {
        dy = 0;
        dx = 1;
    }
    if (dir >= 2) {
        dx *= -1;
        dy *= -1;
    }
}

void forward() {
    y += dy;
    x += dx;
    if (is_valid(x) && is_valid(y))
        visited[x][y] = true;

}

bool turning_position() {

    return turn_states[x][y][dir];
}

bool forward_position() {
    return !turn_states[x][y][dir];
}

nta.system:
// Place template instantiations here.
Process = Template();
// List one or more processes to be composed into a system.
system Process;
